import groovy.json.JsonSlurper;
//Get the request and response data

stpName = context.getCurrentStep().getLabel()
def jReq = new JsonSlurper().parseText(context.getProperty('Request'))
def jRes = new JsonSlurper().parseText(context.getProperty('Response'))

//Save the overall result and The Error Reason if it occurs

//boolean OverallCheck
def validationsPerformed =""

//---------------------------------------------------------Check the line item details between request and response------------------------------------------------------------//
ItemSizeReq = jReq.LineItems.size()
ItemSizeRes = jRes.Order.LineItems.size()
if (ItemSizeReq == ItemSizeRes) {
    validationsPerformed += 'MatchContent_LineItemCount-PASS,'
    for (i = 0; i < ItemSizeReq; i++) {
    reqMS = jReq.LineItems[i].Modifiers.size()
    resMS = jRes.Order.LineItems[i].Modifiers.size()
    if (reqMS == resMS) {
    	 validationsPerformed += 'MatchContent_LineItemNo.'+(i+1)+'_ModifierQuantityCount-PASS,'
      reqMdfiyr = jReq.LineItems[i].Modifiers
      resMdfiyr = jRes.Order.LineItems[i].Modifiers
      reqSid = jReq.LineItems[i].SalesItemId
      resSid = jRes.Order.LineItems[i].SalesItemId
      reqMid = jReq.LineItems[i].MenuItemId
      resMid = jRes.Order.LineItems[i].MenuItemId
      reqIQ = jReq.LineItems[i].Quantity
      resIQ = jRes.Order.LineItems[i].Quantity

      if (reqSid == resSid && reqMid == resMid && reqIQ == resIQ) {
      	validationsPerformed += 'MatchContent_LineItemNo.'+(i+1)+'_SalesItemId-PASS,'
      	validationsPerformed += 'MatchContent_LineItemNo.'+(i+1)+'_MenuItemId-PASS,'
      	validationsPerformed += 'MatchContent_LineItemNo.'+(i+1)+'_LineItemQuantity-PASS,'
        	for (j = 0; j < reqMS; j++) {
          reqMOId = reqMdfiyr[j].SalesItemOptionId
          resMOId = resMdfiyr[j].SalesItemOptionId
          reqMGId = reqMdfiyr[j].ItemOptionGroupId
          resMGId = resMdfiyr[j].ItemOptionGroupId
          reqMn = reqMdfiyr[j].Name
          resMn = resMdfiyr[j].Name
          if (reqMOId == resMOId && reqMGId == resMGId && reqMn == resMn) {
          	validationsPerformed += 'MatchContent_LineItemNo.'+(i+1)+'_ModifierDetailNo.'+(j+1)+'-PASS,'
          } else {
            validationsPerformed += 'MatchContent_LineItemNo.'+(i+1)+'_ModifierDetail.'+(j+1)+'-FAIL,'
          }
        }
      } else {
        validationsPerformed += 'MatchContent_LineItemNo.'+(i+1)+'_ItemIds-FAIL,'
      }
    } else {
      validationsPerformed += 'MatchContent_LineItemNo.'+(i+1)+'_ModifierQuantityCount-FAIL,'
    }
  }
} else {
  validationsPerformed += 'MatchContent_LineItemCount-FAIL,'
}

//---------------------------------------------------------Other Basic Validations------------------------------------------------------------//

if (jRes.HasFAILedItems) {
  validationsPerformed += 'NodePresent_HasFailedItem-FAIL,'
}else{
  validationsPerformed += 'NodePresent_HasFailedItem-PASS,'	
}
if (jRes.OrderId) {
  validationsPerformed += 'NodePresent_OrderId-PASS,'
}else{
	validationsPerformed += 'NodePresent_OrderId-FAIL,'
}
if (jReq.SiteId == jRes.Order.SiteId) {
  validationsPerformed += 'MatchContent_SiteId-PASS,'
}else{
  validationsPerformed += 'MatchContent_SiteId-FAIL,'	
}
if (jReq.PromiseDateTime == jRes.Order.PromiseDateTime) {
  validationsPerformed += 'MatchContent_PromiseTime-PASS,'
}else{
  validationsPerformed += 'MatchContent_PromiseTime-FAIL,'
}
if(!(jReq.Customer.EMail =="noreply@buffalowildwings.com")){
	if (jReq.Customer.EMail == jRes.Order.Customer.EMail) {
  	validationsPerformed += 'MatchContent_Email-PASS,'
	}else{
     validationsPerformed += 'MatchContent_Email-FAIL,'
	}
}else{
	validationsPerformed += 'NodePresent_Email-PASS,'
}

if (jReq.Customer.FirstName == jRes.Order.Customer.FirstName) {
  validationsPerformed += 'MatchContent_FirstName-PASS,'
}else{
  validationsPerformed += 'MatchContent_FirstName-FAIL,'
}
if ((jReq.Customer.LastName).toString() == (jRes.Order.Customer.LastName).toString()) {
  validationsPerformed += 'MatchContent_LastName-PASS,'
}else{
  validationsPerformed += 'MatchContent_LastName-FAIL,'
}
if (((jReq.Customer.VoicePhone).toString()) == jRes.Order.Customer.VoicePhone) {
  validationsPerformed += 'MatchContent_VoicePhone-PASS,'
}else{
  validationsPerformed += 'MatchContent_VoicePhone-FAIL,'
}

