import groovy.json.JsonSlurper;
//Get the request and response data

//CheckScenario Name
//testCaseName
def tcName = messageExchange.modelItem.testStep.testCase.testSuite.getPropertyValue( "testCaseName" )

def validationsPerformed = ''

//SkipPaymentStatus Check if Order is PayAtSite
if (!(tcName.contains('PayAtSite'))){

stpName = context.getCurrentStep().getLabel()
//def jReq = new JsonSlurper().parseText(messageExchange.modelItem.testStep.testCase.testSteps[stpName].getPropertyValue('Request'))
def jReqPOST = new JsonSlurper().parseText(messageExchange.modelItem.testStep.testCase.testSteps[stpName].getPropertyValue('Request'))
def jResPOST = new JsonSlurper().parseText(messageExchange.modelItem.testStep.testCase.testSteps[stpName].getPropertyValue('Response'))

//-----------------------------------------------If Gift CardNode is present check the amount----------------------------------//
if(jReqPOST.GiftCardPayment){
		def gcTotal=0.00
	for (i=0;i<jReqPOST.GiftCardPayment.size();i++){
		gcTotal += jReqPOST.GiftCardPayment[i].Amount
	}
	log.info gcTotal
	rsTotal=jResPOST.FinancialSummary.Total
	if (gcTotal>=rsTotal){
		validationsPerformed +="Check_GiftCardAmount_Greater_ThanTotalAmount-Pass"
	}
	else{
		validationsPerformed= checkPaymentNode()
	}
}
else{
	validationsPerformed= checkPaymentNode()
	}	
}

//----------------------------------------Check the paymentStatus in POST response---------------------------------------------------//
def checkPaymentNode(){
	stpName = context.getCurrentStep().getLabel()
	def validationsPerformed=''
	//def jReq = new JsonSlurper().parseText(messageExchange.modelItem.testStep.testCase.testSteps[stpName].getPropertyValue('Request'))
	def jReqPOST = new JsonSlurper().parseText(messageExchange.modelItem.testStep.testCase.testSteps[stpName].getPropertyValue('Request'))
	def jResPOST = new JsonSlurper().parseText(messageExchange.modelItem.testStep.testCase.testSteps[stpName].getPropertyValue('Response'))

	if (jResPOST.PaymentStatus.Status=='PAID'){
		validationsPerformed += "MatchContent_PaymentStatus-PASS,"
	}else{
		validationsPerformed += "MatchContent_PaymentStatus-FAIL,"
	}
	if (jResPOST.PaymentStatus.Decision=='ACCEPT'){
		validationsPerformed += "MatchContent_DecisionStatus-PASS,"
	}else{
		validationsPerformed += "MatchContent_DecisionStatus-FAIL,"
	}
	if (jResPOST.PaymentStatus.ReasonCode=='100'){
		validationsPerformed += "MatchContent_ReasonCode-PASS,"
	}else{
		validationsPerformed += "MatchContent_ReasonCode-FAIL,"
	}
	if (jResPOST.PaymentStatus.ReasonDesc=='Approved'){
		validationsPerformed += "MatchContent_ReasonDesc-PASS,"
	}else{
		validationsPerformed += "MatchContent_ReasonDesc-FAIL,"
	}
	return validationsPerformed
}

assert(false) : validationsPerformed

