/ *
Method to write
 *
 request response *
 text *
 file *
 /
public void appendStrToFile(String fileName, String str) {
 try {
  File tmpDir = new File(fileName);
  boolean exists = tmpDir.exists();

  if (exists) {
   //log.info "File Already exists... "

   def appendTextFile = context.expand('${#Project#appendTextFile}')

   boolean append = false;
   if (appendTextFile.equalsIgnoreCase("yes")) append = true;

   // Open given file in append mode.
   BufferedWriter out = new BufferedWriter(new FileWriter(fileName, append));
   out.write(str);
   out.close();
  } else {
   // Create given file
   BufferedWriter out = new BufferedWriter(
    new FileWriter(fileName));
   out.write(str);
   out.close();

  }
 } catch (IOException e) {
  //log.info "File Creation exception occoured: " + e;
 }
}

/*
 * Method to create
 * Folder
 * if doesn't exists
 * already
 */

public void createFolderIfNotExistsAlready(String folderAddress) {
 try {
  //Creating folder

  File directory = new File(folderAddress);
  def success = false;
  if (!directory.exists()) {
   //log.info "directory.exists(): " + directory.exists();
   //log.info "Folder doesn't exist, Creating folder: " + folderAddress;
   success = directory.mkdir(); //mkdir command creates directory, and return true if successfully created
   if (!success){} //log.info "Folder Creation: Failed";
  } else {
   //log.info("Folder >>>  " + folderAddress + " This Folder already exist!!!");
   success = true;
  }

 } catch (IOException e) {
  //log.info "Folder creation exception occoured: " + e;
 }
}

