import groovy.json.JsonSlurper;
//Get the request and response data

stpName = context.getCurrentStep().getLabel()

def jResPOST = new JsonSlurper().parseText(messageExchange.modelItem.testStep.testCase.testSuite.getPropertyValue( "POSTResponse" ))
def jResDStatus = new JsonSlurper().parseText(messageExchange.modelItem.testStep.testCase.testSteps[stpName].getPropertyValue('Response'))

def validationsPerformed =""
//---------------------------------------------------------Check the line item details between PUT response and POST response------------------------------------------------------------//

if ((jResPOST.DeliveryStatus.Response.Id).toString() == jResDStatus[0].DeliveryId){
	validationsPerformed += "MatchContent_DeliveryId-PASS,"
}else{
	validationsPerformed += "MatchContent_DeliveryId-FAIL,"
}
if ((jResPOST.DeliveryStatus.Response.EstimatedPickupTime) == jResDStatus[0].EstimatedPickupTime){
	validationsPerformed += "MatchContent_DeliveryEstimatedPickupTime-PASS,"
}else{
	validationsPerformed += "MatchContent_DeliveryEstimatedPickupTime-FAIL,"
}
if ((jResPOST.DeliveryStatus.Response.EstimatedDropoffTime) == jResDStatus[0].EstimatedDropoffTime){
	validationsPerformed += "MatchContent_DeliveryEstimatedDropoffTime-PASS,"
}
else{
	validationsPerformed += "MatchContent_DeliveryEstimatedDropoffTime-FAIL,"
}

assert(OverallCheck) : validationsPerformed

