con:config.script:
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

//---------------------------------------------Setup Script Parameters---------------------------------------------//

myProject= testRunner.testCase.testSuite.project
ScenariosList=myProject.testSuites["Scenarios"]
ReportingLoc= testRunner.testCase.testSuite.testCases["Reporting"]
//ExecutionCase=ExecutionSuite.testCases["Ordering"]

//----------------------------------Setting up the Execution Environment ---------------------------------------//

testRunner.runTestStepByName( "SetupEnvironment")

//----------------------------------------------Clear the Report Logs of previous Execution---------------------------//
log.info ("Cleaning Previous Execution Logs")
testRunner.testCase.setPropertyValue("clearLogs", "All")
testRunner.runTestStepByName( "ClearLogs")

//-----------------------------Get the list of scenarios and test cases  to be executed----------------------------------------------------//

//Get Scenarios list
def scenariosListForExecution= ScenariosList.getPropertyValue("scenariosListForExecution")
//log.info scenariosListForExecution

def scenariosToExecute = scenariosListForExecution.split(',')
//log.info scenariosToExecute[1]
for( i = 0; i<scenariosToExecute.length;i++){
	//Set the test scenario to be executed
	testRunner.testCase.setPropertyValue("scenarioToExecute", scenariosToExecute[i])
	//Get the list of testCases to be execute for the scenario
	def testCasesToExecute= ScenariosList.getPropertyValue( scenariosToExecute[i]).split(',')
	// Individually run every test case for the selected scenario
	for(j=0;j<testCasesToExecute.length;j++){
		//Setting Reporting Params
		scenarioCase = scenariosToExecute[i]+"-"+testCasesToExecute[j]
		ReportingLoc.testSteps["TestStepExecutionResult"].setPropertyValue(scenarioCase,"")
		ReportingLoc.testSteps["TestStepExecutionResult"].setPropertyValue(scenarioCase+"-Result","")
		ReportingLoc.testSteps["TestCaseExecutionComments"].setPropertyValue(scenarioCase,"")
		ReportingLoc.testSteps["TestCaseExecutionResult"].setPropertyValue(scenarioCase,"")
		//Printing Scenario and TestCase Name
		log.info ("--For Scenario--"+scenariosToExecute[i]+"--Executing Test Case--"+testCasesToExecute[j])
		//Setting the TestCase to execute
		testRunner.testCase.setPropertyValue("testCaseToExecute", testCasesToExecute[j])
		testRunner.testCase.setPropertyValue("scenarioCaseName", scenarioCase)
		testRunner.testCase.setPropertyValue("clearLogs", "TestCase")
		//Starting Execution
		testRunner.runTestStepByName("Script2")
		log.info ("Cleaning Current Test Cases Execution Logs")
		testRunner.runTestStepByName( "ClearLogs")
	}

}

//Generate the html report for the executed scenarios

log.info ("-------- Generating Execution Report --------")
createReport("MainPageReport")
createReport("TestCaseReport")
createReport("SaveExecutionReport")

def createReport(scriptName){
	mytestSuite = myProject.getTestSuiteByName("ExecutionSetup")
	testCase = mytestSuite.getTestCaseByName("Reporting")
	testStep=testCase.getTestStepByName(scriptName)
	testRunner = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestCaseRunner(testCase, null);
	testStepContext = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext(testStep);
	testStep.run(testRunner, testStepContext);
}

//Setup the location of Scenario list
myProject= testRunner.testCase.testSuite.project
ScenariosFolder=myProject.testSuites["Scenarios"]

//Get the Name of Current Test Scenario and TestCase to be Executed
def scenarioName= testRunner.testCase.getPropertyValue("scenarioToExecute")
def testCaseName=testRunner.testCase.getPropertyValue("testCaseToExecute")


//Set the testCaseName to APICallsFolder for storing the logs under it
myProject.testSuites["APICalls"].setPropertyValue("scenarioName",scenarioName)
myProject.testSuites["APICalls"].setPropertyValue("testCaseName",testCaseName)

log.info ("Setting up Execution data" )

//Setup the location of Current Scenario and TestCase to be executed
Scenario= ScenariosFolder.testCases[scenarioName]
TestCase=Scenario.testSteps[testCaseName]

//Get the parameters details to be setup for the testCase
stepsToExecuteList= TestCase.getPropertyValue("stepsToExecute").split(",")
PUTCallToUse= TestCase.getPropertyValue("PUTcall")
POSTCallToUse= TestCase.getPropertyValue("POSTcall")
dataSetupList= TestCase.getPropertyValue("setupdata").split(",")
lineItems= TestCase.getPropertyValue("lineItems")

//Setting up test data which is to be used by the current test case for execution

executionDataFolder= myProject.testSuites["ExecutionData"]
for(i=0;i<dataSetupList.length;i++){
	dataChunk = dataSetupList[i].split("-")
	if (dataChunk[1].contains(".")){
		def s = dataChunk[1]
		def replacement = { if (it == '.') { '-' } else { null }}
		dataChunk[1]= s.collectReplacements(replacement)
		}
	executionDataFolder.setPropertyValue(dataChunk[0],dataChunk[1])
}

executionDataFolder.setPropertyValue("PUTCall",PUTCallToUse)
executionDataFolder.setPropertyValue("POSTCall",POSTCallToUse)
executionDataFolder.setPropertyValue("lineItems",lineItems)

//Get the specific assertions if applicable
if ( TestCase.getPropertyValue("specificAssertion")){
	assertions = TestCase.getPropertyValue("specificAssertion")
	executionDataFolder.setPropertyValue("assertionToUse",assertions)
}

//log.info TestCase.getPropertyValue("WalletDetail")
if ( TestCase.getPropertyValue("WalletDetail")){
	walletDetail = TestCase.getPropertyValue("WalletDetail")
	executionDataFolder.setPropertyValue("WalletDetail",walletDetail.toString())
}

for(m=0;m<stepsToExecuteList.length;m++){
	testRunner.testCase.setPropertyValue("runTestStep", stepsToExecuteList[m])
	//Set the step name to save the logs
	myProject.testSuites["APICalls"].setPropertyValue("testStepName",stepsToExecuteList[m])
	// Specific Conditon for Add Certificate check and looping
	testStepName=stepsToExecuteList[m]
	switch(testStepName) { 
  	    case ~/.*Loyalty-AddCertificate.*/:
  	    log.info ("Adding Loyalty Certificates" )
         myLoop(testStepName)
         break
         case ~/.*Init-GC.*/:
         log.info ("Getting Gift Card payment keys" )
        myLoop(testStepName)
         break
         default:   
        testRunner.runTestStepByName( "Script3")
        testRunner.runTestStepByName( "Script4")
        testRunner.runTestStepByName( "Script5")
	}}

//Get the reward codes/ Gift Cards  which are to be applied if applicable
def myLoop(stepName){
	def propValue=""
	if (stepName.contains("AddCertificate")){
		propValue = TestCase.getPropertyValue("rewardCode")
	}
	if (stepName=="Init-GC"){
		propValue = TestCase.getPropertyValue("giftCard")
	}
	lpv= propValue.split(",")
	//log.info("Size: "+lpv.size())
	for (i =0 ; i<lpv.size();i++){
	def replacement = { if (it == '^') { ',' } else { null }}
	rpc= lpv[i].collectReplacements(replacement)
	rc = rpc.split(',')
	pName= rc[0]
	//log.info ("Name: "+ pName)
	pQuant= Integer.parseInt(rc[1])
	//log.info ("Quantity: "+pQuant)
	if (stepName=="Loyalty-AddCertificate"){
		executionDataFolder.setPropertyValue("rewardCode",pName)
	}
	if (stepName=="Init-GC"){
		executionDataFolder.setPropertyValue("giftCard",pName)
	}
	for (j=0; j<pQuant;j++){
		//log.info (j)
		if (stepName=="Init-GC"){
			setGCAmount(pName,j)
		}
		testRunner.runTestStepByName( "Script3")
		testRunner.runTestStepByName( "Script4")
}}
testRunner.runTestStepByName( "Script5")
}
def setGCAmount(cardName,amountIndex){
	cardAmount = TestCase.getPropertyValue("Amount-"+cardName).split(",")[amountIndex]
	executionDataFolder.setPropertyValue("giftCardAmount",cardAmount)
}

import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext
//Get the test case name which is to be executed testStepName= testRunner.testCase.getPropertyValue("runTestStep")
dataSetupFolder= testRunner.testCase.testSuite.project.testSuites["ExecutionData"]
//CallingSpecificScriptsForDataSetup switch(testStepName) { case ~/.*Loyalty.*/: log.info ("Setting up Loyalty Data") runDataScript("DataSetup-Loyalty") break case ~/.*PUT.*/: log.info ("Setting up PUT Request") runDataScript("DataSetup-LineItems") runDataScript("DataSetup-PUT") break case ~/.*POST.*/: log.info ("Setting up POST Request") runDataScript("DataSetup-POST") break case ~/.*Init.*/: log.info ("Setting up Init Request") runDataScript("DataSetup-Init") runDataScript("DataSetup-PayKeys") break
} //CallingTheScript def runDataScript(scriptName) { ScriptToRun=testRunner.testCase.testSuite.testCases["DataSetupScripts"].testSteps[scriptName] testStepContext = new WsdlTestRunContext(ScriptToRun) ScriptToRun.run(testRunner, context) }
