import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

myProject= testRunner.getTestCase().getTestSuite().getProject()
dataSetupFolder= testRunner.testCase.testSuite.project.testSuites["ExecutionData"]
AssertionSuite= myProject.testSuites["Assertions"]

//Calling Generic Assertion Script
log.info ("Validating Mandatory Assertions")
runAssertion("Assertion-Mandatory")

//CallingSpecific Assertion Scripts if applicable
 if(dataSetupFolder.getPropertyValue('assertionToUse')){
 		log.info ("Validating Specific Assertions")
 		 runAssertion("Assertion-Specific")
 }

//Calling the script to add executionlogs
 addReporting("AddExecutionStatus")

def addReporting(scriptName){
	mytestSuite = myProject.getTestSuiteByName("ExecutionSetup")
	testCase = mytestSuite.getTestCaseByName("Reporting")
	testStep=testCase.getTestStepByName(scriptName)
	testRunner = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestCaseRunner(testCase, null);
	testStepContext = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext(testStep);
	testStep.run(testRunner, testStepContext);
}

//Script Function
def runAssertion(scriptName) { 
    ScriptToRun=testRunner.testCase.testSuite.testCases["AssertionScripts"].testSteps[scriptName]
    testStepContext = new WsdlTestRunContext(ScriptToRun)
    ScriptToRun.run(testRunner, testStepContext)
}

myProject= testRunner.testCase.testSuite.project

logsToClear= testRunner.testCase.getPropertyValue("clearLogs")
if(logsToClear=="TestCase"){
	clearData("APICalls")
	clearData("ExecutionData")
	clearData("Assertions")
}
else{
	clearReportData("TestStepExecutionResult")
	clearReportData("TestCaseExecutionComments")
	clearReportData("TestCaseExecutionResult")
	clearData("APICalls")
	clearData("ExecutionData")
	clearData("Assertions")
}

public void clearReportData(stepName){
propertiesSteps = testRunner.testCase.testSuite.testCases["Reporting"].testSteps[stepName]
   propertiesSteps.getPropertyNames().each{ propName ->
    propertiesSteps.removeProperty(propName)
}
}

public void clearData(suiteName){
testSuiteProperties = myProject.testSuites[suiteName]
   testSuiteProperties.getPropertyNames().each{ propName ->
    testSuiteProperties.removeProperty(propName)
}}

