//****************Setting up time for the promise time and pick up time for futuer orders********************//
myProject= testRunner.testCase.testSuite.project

def TimeinMinutes =myProject.getPropertyValue("TimeinMinutes")
//log.info(TimeinMinutes)

TimeinMinutes=Long.parseLong(TimeinMinutes)

def value =  java.time.OffsetDateTime.now()
value =value.plusMinutes((TimeinMinutes))
value =value.format(java.time.format.DateTimeFormatter.ofPattern('yyyy-MM-dd\'T\'HH:mm:ss'))
log.info("PickupTime: "+value)

myProject.setPropertyValue("pTime",value)
myProject.setPropertyValue("pickupTime",value)

//***********************************SETUP EXECUTION ENVIRONMENT********************************//

def environment = myProject.getPropertyValue("environment")
log.info ("Setting up Execution Environment as: "+environment)
propertiesStep =myProject.testSuites["ExecutionData"].testCases["EnvironmentVariable"].getTestStepByName(environment)

for( prop in propertiesStep.propertyNames  )
{
	if(!(prop.contains("-old"))){
		myProject.setPropertyValue( prop , propertiesStep.properties[prop].value )
	}    
}

