//Getting the current date
def now = new Date()

//Setting the project variable
myProject= testRunner.testCase.testSuite.project
executionScript= testRunner.testCase.testSuite.testCases["ExecutionScript"]

//Getting the name and Type of PUT call to be executed for the current test case
PutCallName= executionScript.getPropertyValue("runTestStep")
ExecutionTestSuite= myProject.testSuites["APICalls"]
ExecutionDataFolder= myProject.testSuites["ExecutionData"]
PUTCallType= ExecutionDataFolder.getPropertyValue("PUTCall")

//Get the static properties for the order
def siteId
if (ExecutionDataFolder.getPropertyValue("siteId")){
		siteId= Integer.parseInt(ExecutionDataFolder.getPropertyValue("siteId"))
		ExecutionTestSuite.setPropertyValue("SiteId",siteId.toString())
	}
else{
		siteId= Integer.parseInt(myProject.getPropertyValue("siteId"))
		ExecutionTestSuite.setPropertyValue("SiteId",siteId.toString())
}
//Checking if specific promise time is to be used else using generic promise time
if(ExecutionDataFolder.getPropertyValue("promiseTime")){
	pTime= ExecutionDataFolder.getPropertyValue("promiseTime")
}else{
	pTime= myProject.getPropertyValue("pTime")
}

//Get the profile Id if applicable
def profileId =""
if (PUTCallType.contains("Loyalty")){
//Get the ProfileId for the order	
	if(ExecutionDataFolder.getPropertyValue("profileIdName")){
		profileIdToUSe=ExecutionDataFolder.getPropertyValue("profileIdName")
		ExecutionTestSuite.setPropertyValue("profileIdName",profileIdToUSe)
		profileId=ExecutionDataFolder.testCases["CustomerInformation"].testSteps[profileIdToUSe].getPropertyValue("ProfileId")
	}else if (myProject.getPropertyValue("profileId")){
		profileId= myProject.getPropertyValue("profileId")
		ExecutionTestSuite.setPropertyValue("profileIdName","Profile1")
	}else{
		profileId= ExecutionDataFolder.testCases["CustomerInformation"].testSteps["Profile1"].getPropertyValue("ProfileId")
		ExecutionTestSuite.setPropertyValue("profileIdName","Profile1")
	}
	ExecutionTestSuite.setPropertyValue("profileId",profileId)
}


//Get the line item details
lineItems=ExecutionDataFolder.getPropertyValue("lineItemsDetail")
def jsonLineItem=new JsonSlurper().parseText(lineItems)

//Add the details to the POGO class
OrderNode oNode= new OrderNode()
CustAdd cadd= new CustAdd()

//---------------------------- Create OrderNode--------------------------
oNode.metaClass.SiteId = siteId
oNode.metaClass.PromiseDateTime= pTime
oNode.metaClass.LineItems=jsonLineItem
oNode.metaClass.CustomerAddressForOrder= cadd
oNode.metaClass.MenuId= 1000
oNode.metaClass.OrderMode=1
oNode.metaClass.UpdateTimeDisabled= false
oNode.metaClass.OrderSource= 0
oNode.metaClass.ShouldManualRelease= false
oNode.metaClass.CustomerAddressForOrder= cadd
oNode.metaClass.PaymentMode= 2
oNode.metaClass.orderSiteLoading= false
oNode.metaClass.LoyaltyNumber=""
oNode.metaClass.LoyaltyApplicable=true
oNode.metaClass.ProfileId=""
//---------------------------------------------------------------------------

//Check if loyalty is applicable and add the details
if (PUTCallType.contains("Loyalty")){
	CustomerInfo cinfo = new CustomerInfo()
	def email
	if (ExecutionDataFolder.getPropertyValue("email")){
		email= ExecutionDataFolder.getPropertyValue("email")
	}
	else if (myProject.getPropertyValue("email")){
		email= myProject.getPropertyValue("email")
	}
	else if(ExecutionDataFolder.getPropertyValue("profileIdName")){
		profileIdToUSe=ExecutionDataFolder.getPropertyValue("profileIdName")
		email=ExecutionDataFolder.testCases["CustomerInformation"].testSteps[profileIdToUSe].getPropertyValue("EMail")
	}
	else{
		email= ExecutionDataFolder.testCases["CustomerInformation"].testSteps["Profile1"].getPropertyValue("EMail")
	}
	if (ExecutionDataFolder.getPropertyValue("firstName")){
		firstName=ExecutionDataFolder.getPropertyValue("firstName")
	}else{
		firstName=executionScript.getPropertyValue("scenarioCaseName")	
	}
	lastName= now.format("MMddHHmms", TimeZone.getTimeZone('IST'))
	cinfo.metaClass.EMail= email
	cinfo.metaClass.FirstName= firstName
	cinfo.metaClass.LastName =lastName
	cinfo.metaClass.VoicePhone ="5033801760"
	if (PUTCallType.contains("Pickup")){
//Set the ProfileId to order Node
	oNode.metaClass.ProfileId= profileId	
	}
//Check if loyalty applicable is specifically mentioned or not
	if (ExecutionDataFolder.getPropertyValue("LoyaltyApplicable")){
		boolean lstat= ExecutionDataFolder.getPropertyValue("LoyaltyApplicable").toLowerCase()
		oNode.metaClass.LoyaltyApplicable= lstat
	}
	oNode.metaClass.Customer= cinfo
}else{
	CustomerInfo cinfo = new CustomerInfo()
	//Create Guest User Data
	cinfo.metaClass.EMail= "noreply@buffalowildwings.com"
	cinfo.metaClass.FirstName= "guest"
	cinfo.metaClass.LastName ="guest"
	cinfo.metaClass.VoicePhone ="0"
	//Set data to order node
	oNode.metaClass.Customer= cinfo
}
//---------------------Check the order type i.e. Delivery or Pickup and make changes----------------------
if (PUTCallType.contains("Pickup")){
	pUTRequest = new JsonBuilder(oNode).toPrettyString()
}
else if(PUTCallType.contains("Delivery")){
//Get Static Value
	if(ExecutionDataFolder.getPropertyValue("pickupTime")){
		pkupTime= ExecutionDataFolder.getPropertyValue("pickupTime")
	}else if(ExecutionDataFolder.getPropertyValue("promiseTime")){
		ExecutionDataFolder.getPropertyValue("promiseTime")
	}else{
		pkupTime= myProject.getPropertyValue("pickupTime")	
	}
//-------------------------------Add nodes specific to Delivery----------------------------------------
//Pickup Quote
	PickupDropOffQuote pickupQuote = new PickupDropOffQuote()
	pickupQuote.metaClass.UTCOffset = "UTC-06:00"
	pickupQuote.metaClass.Street="5025 Excelsior Blvd."
	pickupQuote.metaClass.City= "St. Louis Park"
	pickupQuote.metaClass.State= "MN"
	pickupQuote.metaClass.PostalCode="55416"
	pickupQuote.metaClass.Latitude= 44.931058
	pickupQuote.metaClass.Longitude= -93.346002
	pickupQuote.metaClass.Instructions= ""
//DropOff Quote
	PickupDropOffQuote dropOffQuote = new PickupDropOffQuote()
	dropOffQuote.metaClass.Unit = ""
	dropOffQuote.metaClass.Street="5025 Excelsior Blvd."
	dropOffQuote.metaClass.City= "St. Louis Park"
	dropOffQuote.metaClass.State= "MN"
	dropOffQuote.metaClass.PostalCode="55416"
	dropOffQuote.metaClass.Latitude= 44.931058
	dropOffQuote.metaClass.Longitude= -93.346002
	dropOffQuote.metaClass.Instructions= ""
//Options Node 
	OptionsNode opt= new OptionsNode()
	opt.metaClass.WhitelistedDeliveryServices= "favor,mock"
	opt.metaClass.PreferredDeliveryServices= "mock"
	opt.metaClass.MaxDeliveryFee= 5.99
//For Delivery Quote Node
	QuoteNode qNode = new QuoteNode()
	qNode.metaClass.Pickup = pickupQuote
	qNode.metaClass.Dropoff = dropOffQuote
	qNode.metaClass.Options = opt
	qNode.metaClass.NextPickupTime=null
	qNode.metaClass.FutureDropoffTime = pkupTime
//Additional param in oNode
oNode.metaClass.OrderId= null

//Creating delivery request body
	DelObj delReq= new DelObj()
	delReq.metaClass.Order = oNode
	delReq.metaClass.Quote = qNode
	if(PUTCallType.contains("Loyalty")){
		delReq.metaClass.ProfileId = profileId
	}else{
		delReq.metaClass.ProfileId = ""
	}
	delReq.metaClass.LoyaltyApplicable=true
	pUTRequest = new JsonBuilder(delReq).toPrettyString()
}
//Converting the request to string format and storing the data
//log.info ("PUT Request Body:  "+pUTRequest)
myProject.testSuites["APICalls"].testCases["Ordering"].testSteps[PutCallName].setPropertyValue("Request",pUTRequest)

