	}
//Create Customer node
	myReq.metaClass .Customer=myCust
}
else{
	myCust.metaClass .CustomerSince= null
	myCust.metaClass .FirebaseUID= null
	myReq.metaClass .Customer=myCust
}
//Check if channel is provided
	if (ExecutionDataFolder.getPropertyValue("Channel")){
		myCust.metaClass .Channel=ExecutionDataFolder.getPropertyValue("Channel")
	}
	else{
		myCust.metaClass .Channel="Web"
	}

//Check if delivery type is provided
	if (ExecutionDataFolder.getPropertyValue("DeliveryType")){
		myReq.metaClass .DeliveryType=ExecutionDataFolder.getPropertyValue("DeliveryType")
	}
	else {
		def typeName =""
		if(PostCallType.contains("Pickup")){
			typeName = "PickupFuture"
		}
		else if(PostCallType.contains("Delivery")){
			typeName = "DeliveryFuture"
		}
		myReq.metaClass .DeliveryType=typeName
	}

//Common Nodes of the POST request
	myReq.metaClass .SendEmail =true
	myReq.metaClass .PromiseDateTime= ExecutionTestSuite.getPropertyValue("PromiseDateTime")
	myReq.metaClass .LockOrderModification=true
	myReq.metaClass .ASAPDeliveryTimeEnabled=true
	myReq.metaClass .ProfileId=""
	myReq.metaClass .LoyaltyApplicable=true
	myReq.metaClass .MerchantReferenceCode="Merchant: "+now

//Check the payment type and add payment node to the request
	if (PostCallType.contains("Loyalty")){
		myReq.metaClass .ProfileId=profileId
	}
	if (PostCallType.contains("CC")){
		def jsonPayKeys=new JsonSlurper().parseText(ExecutionDataFolder.getPropertyValue("paymentCC"))
		myReq.metaClass .PaymentKeys=jsonPayKeys
		myReq.metaClass .CardIssuer=ExecutionDataFolder.getPropertyValue("cardIssuer")
		myReq.metaClass .MaskedCardNumber=ExecutionDataFolder.getPropertyValue("maskedCardNumber")
	}
	if (PostCallType.contains("GC")){
		def jsonPayKeys=new JsonSlurper().parseText(ExecutionDataFolder.getPropertyValue("paymentGC"))
		myReq.metaClass .GiftCardPayment=jsonPayKeys
	}
	if (PostCallType.contains("Wallet")){
		def jsonPayKeys=new JsonSlurper().parseText(ExecutionDataFolder.getPropertyValue("paymentWallet"))
		myReq.metaClass .PaymentKeys=jsonPayKeys
		myReq.metaClass .CardIssuer=ExecutionDataFolder.getPropertyValue("cardIssuer")
		myReq.metaClass .MaskedCardNumber=ExecutionDataFolder.getPropertyValue("maskedCardNumber")
		myReq.metaClass .CustomerIdentifier=ExecutionDataFolder.getPropertyValue("CustomerIdentifier")
		myReq.metaClass .PaymentToken=ExecutionDataFolder.getPropertyValue("PaymentToken")
	}

//----------------------Add nodes specific to Pickup or Delivery Order-------------------------------
	if (PostCallType.contains("Pickup")){
		myReq.metaClass .orderSourceId = 0
		if(ExecutionDataFolder.getPropertyValue("restaurantTip")){
			myReq.metaClass .RestaurantTip= Integer.parseInt(ExecutionDataFolder.getPropertyValue("restaurantTip"))
		}else{
			myReq.metaClass .RestaurantTip= 0	
		}		
		myReq.metaClass .ApplyStoredValue=true
		myReq.metaClass .UpdateTimeDisabled=true
		myReq.metaClass .AssignLoyalty=false
		pOSTRequest = new JsonBuilder(myReq).toPrettyString()
	}
	else if (PostCallType.contains("Delivery")){
//Create Delivery Node
		Del myDel= new Del()
	if (ExecutionDataFolder.getPropertyValue("DropoffName")){
		myDel.metaClass .DropoffName=ExecutionDataFolder.getPropertyValue("DropoffName")
	}
