		myReq.metaClass .RestaurantNumber=Integer.parseInt(ExecutionDataFolder.testCases["RestaurantDetail"].testSteps["RestaurantDetails"].getPropertyValue("RestaurantNumber-"+siteId))
		myReq.metaClass .RestaurantName=ExecutionDataFolder.testCases["RestaurantDetail"].testSteps["RestaurantDetails"].getPropertyValue("RestaurantName-"+siteId)
