import groovy.json.JsonSlurper
import groovy.json.JsonBuilder

ExecutionDataFolder= testRunner.testCase.testSuite.project.testSuites["ExecutionData"]

// Getting the line items to be included for the request
itemsToOrder=  ExecutionDataFolder.getPropertyValue("lineitems").split(",")
itemsCategories = ExecutionDataFolder.testCases["LineItems"].getTestStepList()

//Items list for PUT request
itemsForPUTOrder =[itemsToOrder.length]

//Get the line items which are to be added 
for(i=0;i<itemsToOrder.length;i++){
	itmNmQunt =  itemsToOrder[i]
	def replacement = { if (it == '^') { ',' } else { null }}
	itmNmQuntUp=itmNmQunt.collectReplacements(replacement)
	 itemName = itmNmQuntUp.split(",")[0]
	 //log.info itemName
	 itemQuantity = itmNmQuntUp.split(",")[1]
	 //log.info itemQuantity
//loop through the itemsCategories to search for the item
for(item  in  itemsCategories)
  {
  	def itemDetails=item.getPropertyValue(itemName)
  	//Set the lineItemNumber and Quantity for each of the items
  	if(!(itemDetails.equals(null))){
  		//log.info itemDetails
  		def jsonLineItemsDetails=new JsonSlurper().parseText(itemDetails)
  		jsonLineItemsDetails.ItemLineNumber = i+1
  		jsonLineItemsDetails.Quantity=Integer.parseInt(itemQuantity)
  		jsonLineItemsDetails.SpecialInstructions="ItemName: "+itemName
  		//log.info jsonLineItemsDetails
  		//Storing the lineitems for PUT order request
  		jsonStringFormat= new JsonBuilder( jsonLineItemsDetails ).toPrettyString()
  		itemsForPUTOrder[i]=jsonStringFormat
  	}
  }
}
//log.info itemsForPUTOrder
// Transfer the items detail for PUT request
ExecutionDataFolder.setPropertyValue("lineitemsDetail",itemsForPUTOrder.toString())

import groovy.json.JsonSlurper
import groovy.json.JsonBuilder
import groovy.json.JsonOutput



//Setting the project variable
executionScript= testRunner.testCase.testSuite.testCases["ExecutionScript"]

//Getting the name and type of  init to be used 
ExecutionDataFolder= testRunner.testCase.testSuite.project.testSuites["ExecutionData"]
ExecutionTestSuite= testRunner.testCase.testSuite.project.testSuites["APICalls"]
InitCallName= executionScript.getPropertyValue("runTestStep")
cardDataFolder= testRunner.testCase.testSuite.project.testSuites["ExecutionData"].testCases["CardData"]

//Condition to check which init to run 
  switch(InitCallName) {            
         case ~/.*Init-CC.*/:
         setDataCC("Card")
         break
         case ~/.*Init-GC.*/:
         setDataCC("GiftCard")
         break
         case ~/.*Init-Wallet.*/:
         setDataCC("CardOnFile")
         break
      }

def setDataCC(initType){
	InitBody initReq= new InitBody()
	if((initType=="Card")||(initType=="CardOnFile")){
		initReq.metaClass.ButtonText="Pay"
	}
	if(initType=="GiftCard"){
		initReq.metaClass.ButtonText="Save"
	}
	initReq.metaClass.PaymentType=initType
	initReq.metaClass.OrderId=Integer.parseInt(ExecutionTestSuite.getPropertyValue("orderId"))
	initRequest = new JsonBuilder(initReq).toPrettyString()
	//log.info initRequest
	ExecutionTestSuite.testCases["Payments"].testSteps["Payment-Init"].setPropertyValue("Request",initRequest)
}

class InitBody{}

import groovy.json.JsonSlurper
import groovy.json.JsonBuilder
import groovy.json.JsonOutput

//Setting the project variable
executionScript= testRunner.testCase.testSuite.testCases["ExecutionScript"]

//Getting the name and type of  init to be used 
ExecutionDataFolder= testRunner.testCase.testSuite.project.testSuites["ExecutionData"]
ExecutionTestSuite= testRunner.testCase.testSuite.project.testSuites["APICalls"]
InitCallName= executionScript.getPropertyValue("runTestStep")
cardDataFolder= testRunner.testCase.testSuite.project.testSuites["ExecutionData"].testCases["CardData"]

cardToUse=""
myCardData="123" //set to 5541 for wallet
maskedCardNumber=""

//Condition to check which PayKey Data Setup has to be done
  switch(InitCallName) {            
         case ~/.*Init-CC.*/:
         if(ExecutionDataFolder.getPropertyValue("card")){
         	cardToUse=ExecutionDataFolder.getPropertyValue("card")
         }else{
         	cardToUse= "Visa"
         }
         myCardData=searchCard(cardToUse)
         createBody()
         break
         case ~/.*Init-GC.*/:
         if(ExecutionDataFolder.getPropertyValue("giftCard")){
         	cardToUse=ExecutionDataFolder.getPropertyValue("giftCard")
         }else{
         	cardToUse= "GiftCard1"
         }
         myCardData=searchCard(cardToUse)
         createBody()
         break
         case ~/.*Init-Wallet.*/:
         createBody()
         break
      }

def createBody(){
	PaymentBody pBody = new PaymentBody()
	 switch(InitCallName) {            
         case ~/.*Init-CC.*/:
		pBody.metaClass.cardData=myCardData
		pBody.metaClass.cardIssuer=8
		pBody.metaClass.paymentType=1
		 if(ExecutionDataFolder.getPropertyValue("postalCode")){
         	pBody.metaClass.postalCode=ExecutionDataFolder.getPropertyValue("postalCode")
         }else{
         	pBody.metaClass.postalCode="55435"
         }
         break
         case ~/.*Init-GC.*/:
         Atbute atb= new Atbute()
         atb.metaClass.CardIssuer= "Unknown"
         atb.metaClass.MaskedCardNumber= maskedCardNumber
         pBody.metaClass.attributes=atb
         pBody.metaClass.cardData=myCardData
         pBody.metaClass.postalCode=null
         pBody.metaClass.paymentType=3
         break
         case ~/.*Init-Wallet.*/:
         pBody.metaClass.attributes=null
         pBody.metaClass.cardData=myCardData
         pBody.metaClass.postalCode=null
         pBody.metaClass.paymentType=2
         setupWalletToken()
         break
	 }
	paymentRequest = new JsonBuilder(pBody).toPrettyString()
	ExecutionTestSuite.testCases["Payments"].testSteps["Payment-GetPayKeys"].setPropertyValue("Request",paymentRequest)
}

//Get the data of the card which is to be used
def searchCard(cardName){
cardList= cardDataFolder.getTestStepList()
for(card in cardList)
  {
   if( card.getPropertyValue("CardData-"+cardName))
   {
	cardDataToUse = card.getPropertyValue("CardData-"+cardName)
	maskedCardNumber = card.getPropertyValue("MaskedCardNumber-"+cardName)
//Get Additional Card Details Specific to Credit Cards
	if (InitCallName=="Init-CC"){
		cardIssuer = card.getPropertyValue("CardIssuer-"+cardName)
		ExecutionDataFolder.setPropertyValue("MaskedCardNumber",maskedCardNumber)
		ExecutionDataFolder.setPropertyValue("CardIssuer",cardIssuer)
	}
	if (InitCallName=="Init-GC"){
		ExecutionDataFolder.setPropertyValue("MaskedCardNumberGC",maskedCardNumber)
	}
return cardDataToUse
}}}

def setupWalletToken(){
	custCard=ExecutionDataFolder.getPropertyValue("WalletDetail").split("-")
	cust=custCard[0]
	wcard=custCard[1]
	customerLocation=ExecutionDataFolder.testCases["CustomerInformation"].testSteps[cust]
//Get Card information
	ExecutionDataFolder.setPropertyValue("CustomerIdentifier",customerLocation.getPropertyValue("FirebaseUID"))
	ExecutionDataFolder.setPropertyValue("PaymentToken",customerLocation.getPropertyValue("WalletToken-"+wcard))
	ExecutionDataFolder.setPropertyValue("MaskedCardNumber",customerLocation.getPropertyValue("MaskedCardNo-"+wcard))
	ExecutionDataFolder.setPropertyValue("CardIssuer",customerLocation.getPropertyValue("CardIssuer-"+wcard))
}

