//CallingSpecificScriptsForDataSetup
  switch(testStepName) {            
         case ~/.*CreateToken.*/:
         executeStep("Loyalty-CreateToken")
         break
         case ~/.*AddCertificate.*/:
         executeStep("Loyalty-AddCertificate")
         break
         case ~/.*CheckCertificate.*/:
         executeStep("Loyalty-CheckCertificate")
         break
         case ~/.*CheckPointBalance.*/:
         executeStep("Loyalty-CheckPointBalance")
         break
      }

def executeStep(executeTheStep) { 
//Find the test case in the API call  which needs to be executed and Run it
testCaseList= ExecutionTestSuite.getTestCaseList()
for(tc in testCaseList)
{
for(ts in  tc.getTestStepList())
  {
   if( ts.getName()==executeTheStep)
   {
    testStepLocation=ExecutionTestSuite.testCases[tc.getName()].testSteps[testStepName]
    testStepContext = new WsdlTestRunContext(testStepLocation)
    testStepLocation.run(testRunner, testStepContext)
    //If the test step is CreateToken then transfer the value to the test suite for further usage
    if (executeTheStep=="Loyalty-CreateToken"){
    	tokenTransfer(testStepLocation)
    }}}}}

//Token transfer function
def tokenTransfer(testStepLocation){
	reponseData=testStepLocation.getPropertyValue("response")
    	def jsonRequest=new JsonSlurper().parseText(reponseData)
    	//log.info ("Created Loyalty Access Token:  "+jsonRequest.AccessToken)
    	ExecutionTestSuite.setPropertyValue("loyaltyAccessToken", jsonRequest.AccessToken)
}

import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext
import groovy.json.JsonSlurper

//Get the test case name which is to be executed
myProject= testRunner.testCase.testSuite.project
executionScript= testRunner.testCase.testSuite.testCases["ExecutionScript"]
testStepName= executionScript.getPropertyValue("runTestStep")

//Get the executionFolder
ExecutionTestSuite= myProject.testSuites["APICalls"]
ExecutionDataSuite= myProject.testSuites["ExecutionData"]

//Find the test case in the API call  which needs to be executed and Run it
testCaseList= ExecutionTestSuite.getTestCaseList()

for(tc in testCaseList)
{
for(ts in  tc.getTestStepList())
  {
   if( ts.getName()==testStepName)
   {
    testStepLocation=ExecutionTestSuite.testCases[tc.getName()].testSteps[testStepName]
    testStepContext = new WsdlTestRunContext(testStepLocation)
    testStepLocation.run(testRunner, testStepContext)
    reponseData=testStepLocation.getPropertyValue("response")
    //Capture the details which needs to be used for further calls
    captureResponseDetails(reponseData)
  }
 }
}

def captureResponseDetails(reponseData){
    	def jsonRequest=new JsonSlurper().parseText(reponseData)
    	//Capture the details from the response
    	customerId= jsonRequest.Order.Customer.CustomerId
    	orderId = jsonRequest.OrderId
    	orderAmount= jsonRequest.Order.TotalAmount
    	promiseDateTime= jsonRequest.Order.PromiseDateTime
    	tipAmount=0.00
//Check if any tips are applicable to the order
if (testStepName.contains("Delivery")){
	if (ExecutionDataSuite.getPropertyValue("driverTip")){
		tipAmount=Double.parseDouble(ExecutionDataSuite.getPropertyValue("driverTip"))
	}
	quoteId= jsonRequest.DeliveryStatus.Response.QuoteId
	ExecutionTestSuite.setPropertyValue("quoteId", quoteId.toString())
}
if (testStepName.contains("Pickup")){
	if (ExecutionDataSuite.getPropertyValue("restaurantTip")){
		tipAmount=Double.parseDouble(ExecutionDataSuite.getPropertyValue("restaurantTip"))
	}
}
orderAmountWithTip= orderAmount+tipAmount


    	//Printing the values to console
    	log.info "OrderId:  "+orderId
    	log.info "Order Amount:  "+orderAmount
    	log.info "Order Amount With Tip:  "+orderAmountWithTip
    	log.info "PromiseDateTime:  "+promiseDateTime
    	// Set the values to the Execution suite for further use
    	ExecutionTestSuite.setPropertyValue("orderId", orderId.toString())
    	ExecutionTestSuite.setPropertyValue("orderAmountWithTip", orderAmountWithTip.toString())
    	ExecutionTestSuite.setPropertyValue("promiseDateTime", promiseDateTime)
    	ExecutionTestSuite.setPropertyValue("customerId", customerId)
}

