def fetchsession(testStepLocation){
	def headerName=""
	if (testStepName=="Init-GPay"){ headerName="x_hpc_session_key" }
	else{ headerName="X_HPC_SESSION_KEY" }
	def sessionkey = testStepLocation.testRequest.response.responseHeaders[headerName]
	ExecutionTestSuite.setPropertyValue("sessionkey", sessionkey[0])
	if (testStepName=="Init-GC"){
	ExecutionDataFolder.setPropertyValue("sessionkeyGC", sessionkey[0])
	}
}

