//Get the test case name which is to be executed myProject= testRunner.testCase.testSuite.project executionScript= testRunner.testCase.testSuite.testCases["ExecutionScript"] testStepName= executionScript.getPropertyValue("runTestStep") //Get the executionFolder ExecutionDataFolder= testRunner.testCase.testSuite.project.testSuites["ExecutionData"] ExecutionTestSuite= testRunner.testCase.testSuite.project.testSuites["APICalls"]
