def fetchPayKeys(){ rspnsData=PaymentStep.getPropertyValue("response") jRspnsData=new JsonSlurper().parseText(rspnsData) switch(testStepName) { case ~/.*Init-CC.*/: keyArray=[2] keyArray[0]='"'+jRspnsData.PaymentKeys[0].toString()+'"' keyArray[1]='"'+jRspnsData.PaymentKeys[1].toString()+'"' ExecutionDataFolder.setPropertyValue("PaymentCC",keyArray.toString()) break case ~/.*Init-Wallet.*/: keyArray=[2] keyArray[0]='"'+jRspnsData.PaymentKeys[0].toString()+'"' keyArray[1]='"'+jRspnsData.PaymentKeys[1].toString()+'"' ExecutionDataFolder.setPropertyValue("paymentWallet",keyArray.toString()) break case ~/.*Init-GC.*/: myGCBody=makeGCNode(jRspnsData) saveGCBody(myGCBody) } }
