//get the locations
project = testRunner.getTestCase().getTestSuite().getProject()
dataSetupFolder= testRunner.testCase.testSuite.project.testSuites["ExecutionData"]
assertionSuite = project.getTestSuiteByName("Assertions")

//Get the scenario name
scenarioName= assertionSuite.getPropertyValue("scenarioCaseName").split("-")[0]
testCase = assertionSuite.getTestCaseByName(scenarioName)
commonAssertions = assertionSuite.getTestCaseByName("ReusableAssertions")

if(dataSetupFolder.getPropertyValue('assertionToUse')){
 	assertionList = dataSetupFolder.getPropertyValue('assertionToUse').split(',')
 	for(assertion in assertionList){
 		if(commonAssertions.getTestStepByName(assertion)){
 			testCase = commonAssertions
 		}
 		 runAssertion(assertion)
 	}
 }


// run test case
def runAssertion(scriptName){
	testStep=testCase.getTestStepByName(scriptName)
	testRunner = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestCaseRunner(testCase, null);
	testStepContext = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext(testStep);
	testStep.run(testRunner, testStepContext);
}

myProject= testRunner.testCase.testSuite.project
executionScript= testRunner.testCase.testSuite.testCases["ExecutionScript"]
testStepName= executionScript.getPropertyValue("runTestStep")

switch(testStepName) { 
         case ~/.*Init.*/:
         assertionResults = doAssertions("Payment-Init")
         assertionResults +=","+doAssertions("Payment-GetPayKeys")
         testStepName= executionScript.getPropertyValue("runTestStep")
         setAssertionStatus(assertionResults,testStepName)
         break
         default:
         assertionResults= doAssertions(testStepName)
         setAssertionStatus(assertionResults,testStepName)
         break
      }

def doAssertions(apiName){
//Get the test case name which is to be executed
myProject= testRunner.testCase.testSuite.project
executionScript= testRunner.testCase.testSuite.testCases["ExecutionScript"]
testStepName= apiName
//log.info testStepName
scenarioCaseName= executionScript.getPropertyValue("scenarioCaseName")
//Get the executionFolder
ExecutionTestSuite= myProject.testSuites["APICalls"]
AssertionSuite= myProject.testSuites["Assertions"]

//Variable to store the stepStatus
def stepStatus="P"
def assertionResults=""
def validationString=""

//Find the test case in the API call  which needs to be validated
testCaseList= ExecutionTestSuite.getTestCaseList()

for(tc in testCaseList)
{
for(ts in  tc.getTestStepList())
  {
   if( ts.getName()==testStepName)
   {
   	def testStepLocation = ExecutionTestSuite.testCases[tc.getName()].getTestStepByName(ts.getName())
	testStepSrc = testStepLocation
	Assertioncounter = testStepSrc.getAssertionList().size()
	for (AssertionCount in 0..Assertioncounter-1)
	{
//Build the Assertion Result as per the steps and scripts	
		label = testStepSrc.getAssertionAt(AssertionCount).getName().toString()
	     status = testStepSrc.getAssertionAt(AssertionCount).getStatus().toString()

//Add the independent assertion used in the validation string except for mandatory and payment
		if ((!(label.contains("PaymentValidations")))&& (!(label.contains("Mandatory")))&&(!(label.contains("SaveLogs")))){
			validationString += label+"-"+status+","
			if (status.contains("FAIL")){
				stepStatus="F"
			}
		}
		//log.info validationString
	     //log.info ("Assertion: "+label +" :: Status: "+status)
	     def error
	     if ((label.contains("Mandatory"))|| (label.contains("PaymentValidations"))){
	     	error = testStepSrc.getAssertionAt(AssertionCount).getErrors()[0].getMessage().toString().split('. E')[0]
	     	error = error?.endsWith(',') ? modifyString(error) : error
	     	if(error.contains("null object")){
	     		if (label.contains("Mandatory")){
	     			error = "Mandatory validations not performed (Invalid Response)-FAIL"
	     		}else if (label.contains("PaymentValidations")){
	     			error = "Payment validations not performed (Invalid Response)-FAIL"
	     		}else{
	     			error = "Response cannot be validated-Fail"
	     		}
	     	}
	     	aList = error
	     	validationString += aList+","
	     	if (error.contains("FAIL")){
				stepStatus="F"
			}
	     }else{
	     	error = testStepSrc.getAssertionAt(AssertionCount).getErrors()
	     }
	 }
assertionResults = validationString
//Update the string to remove the last characters (,)
	if (!(assertionResults.isEmpty())){
 		assertionResults= modifyString(assertionResults)
	}
AssertionSuite.setPropertyValue("stepStatus",stepStatus)
AssertionSuite.setPropertyValue("scenarioCaseName",scenarioCaseName)
return assertionResults
}}}
}
//delete last character of the string
def modifyString(updateString){
	StringBuilder sb = new StringBuilder(updateString)
	 	sb.deleteCharAt(updateString.length() - 1)
 		updateString= sb.toString()
 		return updateString
}

def setAssertionStatus(assertionResults, testStepName){
	myProject= testRunner.testCase.testSuite.project
	AssertionSuite= myProject.testSuites["Assertions"]
	//log.info("Assertions Done: "+assertionResults)
	AssertionSuite.setPropertyValue("stepAssertions",assertionResults)
	AssertionSuite.setPropertyValue("testStepName",testStepName)
}

myProject = testRunner.testCase.testSuite.project
AssertionSuite= myProject.testSuites["Assertions"]

sCName= AssertionSuite.getPropertyValue("scenarioCaseName")
tSName= AssertionSuite.getPropertyValue("testStepName")
sStatus= AssertionSuite.getPropertyValue("stepStatus")
sAssertions= AssertionSuite.getPropertyValue("stepAssertions")

tSExeRes= testRunner.testCase.testSteps["TestStepExecutionResult"]
tCExeCom= testRunner.testCase.testSteps["TestCaseExecutionComments"]
tCExeRes= testRunner.testCase.testSteps["TestCaseExecutionResult"]


updateStepResult()
updateTCResult()
updateTCComments()

def updateStepResult(){
	steps=tSExeRes.getPropertyValue(sCName)
	status=tSExeRes.getPropertyValue(sCName+"-Result")
	acomm=tSExeRes.getPropertyValue(sCName+"-Comments")
	if (steps==null){
		steps=""
	}
	if (status==null){
		status=""
	}
	if (acomm==null){
		acomm=""
	}
	steps +=tSName +","
	status +=sStatus +","
	acomm +=sAssertions+"`"
	tSExeRes.setPropertyValue(sCName,steps )
	tSExeRes.setPropertyValue(sCName+"-Result",status)
	tSExeRes.setPropertyValue(sCName+"-Comments",acomm)

}

def updateTCResult(){
	currentStat= tSExeRes.getPropertyValue(sCName+"-Result")
	if (currentStat.contains("F")){
		tCExeRes.setPropertyValue(sCName,"F" )
	}else{
		tCExeRes.setPropertyValue(sCName,"P" )
	}
}

def updateTCComments(){
	if (sStatus.contains("F")){
		cmmnt= tCExeCom.getPropertyValue(sCName)
		if ((cmmnt== "")||(cmmnt== null)|| (cmmnt.contains("Successfully_Executed"))){
			cmmnt="Failed in Step : " + tSName +", "
			tCExeCom.setPropertyValue(sCName,cmmnt )
		}else{
			cmmnt +=tSName +","
			tCExeCom.setPropertyValue(sCName,cmmnt )
		}}
	if (sStatus.contains("P")){
		cmmnt= tCExeCom.getPropertyValue(sCName)
		if ((cmmnt== "")||(cmmnt== null)||cmmnt.contains("Successfully_Executed")){
			tCExeCom.setPropertyValue(sCName,"Successfully_Executed" )
		}
	}
}

