//------------------------Get the current Date---------------------------------------------//

def date = new Date()
def sdf = new SimpleDateFormat("MM/dd/yyyy")
fDate =sdf.format(date)

//--------------------------------------Get The Execution Environment--------------------------//

exeEnv= myProject.getPropertyValue("environment")

//-----------------------------------------Get the result file---------------------------------------------//
testCaseExeResults = testRunner.testCase.testSteps["TestCaseExecutionResult"].getPropertyList()
//--------------------------count the total number of test cases which were executed--------------------//

testCasesExecuted = testCaseExeResults.size()
//log.info "testCasesExecuted: "+testCasesExecuted

//--------------------------Count the number of failed and passed test cases----------------------------//

testCasePassed = 0
testCaseFailed = 0

for (values in testCaseExeResults) {
  testCaseResult = values.getValue()
  if (testCaseResult == "P") {
    testCasePassed += 1
  }
  if (testCaseResult == "F") {
    testCaseFailed += 1
  }
}

//log.info testCasePassed
//log.info testCaseFailed

//--------------------------Count the total Number of Scenarios executed---------------------------//

//Split the scenarioTCList to get only the scenarios name
scenariosExecuted = []
for (values in testCaseExeResults) {
  scenarios = (values.getName()).split("-")
  scenariosExecuted += scenarios[0]
}
//From the list of scenariosExecuted get only the unique scenarios name
uniqueScenarios = scenariosExecuted.unique()
//for(i in uniqueScenarios){log.info i}

//------------------------------------------------------Generate the Summary data for Main Report ---------------------------------------------------------//

myExecutionSummary = new ExecutionSummary()
myExecutionSummary.metaClass.TotalScenariosExecuted = uniqueScenarios.size()
myExecutionSummary.metaClass.TestCasesExecuted = testCasesExecuted
myExecutionSummary.metaClass.TotalPassed = testCasePassed
myExecutionSummary.metaClass.TotalFailed = testCaseFailed
myExecutionSummary.metaClass.ExecutionDate = fDate
myExecutionSummary.metaClass.ExecutionEnvironment = exeEnv 

myExecutionSummaryJson = new JsonBuilder(myExecutionSummary).toPrettyString()
testRunner.testCase.setPropertyValue("SummaryReport", myExecutionSummaryJson)

//------------------------------------------------------Generate the Scenario Table data for Main Report ---------------------------------------------------------//
myScenarioReport = new ScenarioReport()
List < Scenarios > myScenarios = new ArrayList < Scenarios > ()

for (uniqueScenario in uniqueScenarios) {
  scenario = new Scenarios()
  passCount = 0
  failCount = 0
  testCaseInEach = 0
  List < TestCase > myTestCases = new ArrayList < TestCase > ()

  for (testCase in testCaseExeResults) {
    if (testCase.getName().contains(uniqueScenario)) {
      testCaseInEach++;
      myTestCase = new TestCase()
      myTestCase.TestCaseName = testCase.getName().minus(uniqueScenario+"-")
      myTestCase.StepsExecuted = testRunner.testCase.testSuite.project.getPropertyValue("basePath") // Need to change this to reporting location
      if (testCase.getValue() == "P") {
        myTestCase.Status = "Pass"
        passCount++
      }
      else if (testCase.getValue() == "F") {
        myTestCase.Status = "Fail"
        failCount++
      }
      comments= testRunner.testCase.testSteps["TestCaseExecutionComments"].getPropertyValue(testCase.getName())
      myTestCase.Comments = comments
      //Assign the test case to the test case list 
      myTestCases += myTestCase
    }
  }
  scenario.TestCaseExecuted = testCaseInEach
  scenario.Passed = passCount
  scenario.Failed = failCount
  scenario.TestCases = myTestCases
  //log.info uniqueScenario.getClass()

 //Assign the scenario to POGO
 myScenarioReport.metaClass."${uniqueScenario}"= scenario
}
myScenarioReportJson = new JsonBuilder(myScenarioReport).toPrettyString()
testRunner.testCase.setPropertyValue("ScenarioReport", myScenarioReportJson)

