//-----------------------------------Defining POGO ------------------------------------------//

//-------------------Summary Table Object-----------------------------------//
class ExecutionSummary {
}

//-------------------Scenario Table Object-----------------------------------//
class ScenarioReport {
}

class Scenarios {
  String TestCaseExecuted;
  String Passed;
  String Failed;
  List < TestCase > TestCases;
}

class TestCase {
  String TestCaseName;
  String StepsExecuted;
  String Status;
  String Comments;
}

import groovy.json.JsonBuilder
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

myProject = testRunner.testCase.testSuite.project
executionFolder = myProject.testSuites["APICalls"]
testCaseName = executionFolder.getPropertyValue("testCaseName")

//-----------------------------------------Get the result file---------------------------------------------//
testStepExeResults = testRunner.testCase.testSteps["TestStepExecutionResult"].getPropertyList()
testCaseExeResults = testRunner.testCase.testSteps["TestCaseExecutionResult"].getPropertyList()
testCaseExeComments = testRunner.testCase.testSteps["TestCaseExecutionComments"].getPropertyList()

//Get the unique scenarios for the report
scenariosExecuted = []
for (values in testStepExeResults) {
  scenarios = []
  scenarios = (values.getName()).split("-")
  scenariosExecuted += scenarios[0]
}
//From the list of scenariosExecuted get only the unique scenarios name
uniqueScenarios = scenariosExecuted.unique()
//for(i in uniqueScenarios){log.info i}

//----------------------------------Generate the TestCasesExecutionReport-------------------------------------------------//

testCaseExecutionReport = new TestCaseExecutionReport()
//Create a test cases list object to store the with step data\

for (uniqueScenario in uniqueScenarios) {
  List < TestCase > testCases = new ArrayList < TestCase > ()
  for (testCase in testStepExeResults) {
    check1 = testCase.getName().contains(uniqueScenario) //Check if scenario matches the description
    check2 = !(testCase.getName().contains("Result")) //Check if its not the result
    check3 = !(testCase.getName().contains("Comments")) //Check if its not the comment
    if (check1 && check2 && check3) {
      myTestCase = new TestCase()
      //Get the test case name
      myTestCase.TestCaseName = testCase.getName().minus(uniqueScenario + "-")
      //Store the steps executed for this test case to an array
      myTestSteps = (testCase.getValue()).split(",")
      //Store the results for the steps into an array
      resultData = testRunner.testCase.testSteps["TestStepExecutionResult"].getPropertyValue(testCase.getName() + "-Result")
      myTestStepsResult = resultData.split(",")
      //Store the comment data for the steps into an array
      commentData = testRunner.testCase.testSteps["TestStepExecutionResult"].getPropertyValue(testCase.getName() + "-Comments")
      myCommentResult = commentData.split("`")
      //Create a list to save the steps inside of every test case
      List < TestStep > testSteps = new ArrayList < TestStep > ()
      //Loop through the array list and get the steps and results
      for (counter = 0; counter < myTestSteps.size(); counter++) {
        //Create a test step object to add the test step name and status individually
        myTestStep = new TestStep()
        myTestStep.StepName = myTestSteps[counter]
        //Get the status and change it to a value
        thisStatus = myTestStepsResult[counter]
        //Get the assertion comment for the step
        thisComment = myCommentResult[counter]
        switch (thisStatus) {
        case "P":
          thisStatus = "Pass"
          break
        case "F":
          thisStatus = "Fail"
          break
        }
        myTestStep.Status = thisStatus
        myTestStep.Assertions=thisComment
        //Save the results for the test step into the array list
        testSteps += myTestStep
      }
      //Save the information of the steps executed to the test case
      myTestCase.StepsExecuted = testSteps
      //Save the results for the test case into the array list
      testCases += myTestCase
    } //Check1&Check2 
  } //TestCaseloop
  //Save the result under the specific scenario
  testCaseExecutionReport.metaClass."${uniqueScenario}" = testCases
} //Scenario loop

//Export the report 
testCaseExecutionReportJson = new JsonBuilder(testCaseExecutionReport).toPrettyString()
testRunner.testCase.setPropertyValue("TestCaseReport", testCaseExecutionReportJson)


//Save the json to an external folder
