import groovy.json.JsonSlurper
myProject= testRunner.testCase.testSuite.project
executionScript= myProject.testSuites["ExecutionSetup"].testCases["ExecutionScript"]
ExecutionTestSuite= myProject.testSuites["APICalls"]
testStepName= executionScript.getPropertyValue("runTestStep")
assertionSuite = testRunner.testCase.testSuite

if (testStepName.contains("PUT")){
	stpStat ="P"
	stpAssrtions = assertionSuite.getPropertyValue("stepAssertions")+",";
	thisStep =ExecutionTestSuite.testCases["Ordering"].testSteps[testStepName]
	req = new JsonSlurper().parseText(thisStep.getPropertyValue("Request"))
	res = new JsonSlurper().parseText(thisStep.getPropertyValue("Response"))
//Check Certificate Details
	if (res.LoyaltyStatus.Status){
		if (res.LoyaltyStatus.Status.contains("SUCCESS")){
			stpAssrtions += "MatchContent_LoyaltyStatus-PASS,"
		}else{
			stpAssrtions += "MatchContent_LoyaltyStatus-FAIL,"
			stpStat ="F"
		}
	}
	if (res.LoyaltyStatus.CertificateDetails){
		matchCount = 0
		myCerts = res.LoyaltyStatus.CertificateDetails
		for (cert=0; cert<myCerts.size ; cert++){
			salesItemId = myCerts[cert].SalesItemId
			reqLineitems = req.LineItems
			for(lItems=0; lItems<reqLineitems.size;lItems++){
				lItemSalesId = reqLineitems[lItems].SalesItemId
				if (salesItemId == lItemSalesId){
					stpAssrtions += "MatchContent_AppliedCertificateSalesItemId-PASS,"
					matchCount++
					break
				}
			}
		}
		if(matchCount == 0){
		  stpAssrtions += "MatchContent_AppliedCertificateSalesItemId-FAIL,"
		  stpStat ="F"
		}
	}
	assertionSuite.setPropertyValue("stepStatus", stpStat);
	assertionSuite.setPropertyValue("testStepName", testStepName)
	assertionSuite.setPropertyValue("stepAssertions", modifyString(stpAssrtions))
}

def modifyString(updateString){
	StringBuilder sb = new StringBuilder(updateString)
	 	sb.deleteCharAt(updateString.length() - 1)
 		updateString= sb.toString()
 		return updateString
}

